var mongoose = require('mongoose');
mongoose.connect(process.env.MONGOLAB_URI || 'mongodb://localhost/grapevine');

var locTweetsSchema = mongoose.Schema({
  tweets: [String],
  sentiments: [String],
  num_tweets: Number,
  avg_sentiment: Number,
  loc: [Number]
});

var LocTweets = mongoose.model('LocTweets', locTweetsSchema);

var keywordSchema = mongoose.Schema({
  locationTweets: [{ type: mongoose.Schema.Types.ObjectId, ref: 'LocTweets' }],
  keyword: String
});

var Keyword = mongoose.model('Keyword', keywordSchema);

module.exports.Tweet = LocTweets;
module.exports.Keyword = Keyword;
